import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  password = '';
  clicked = 0;
  letters = false;
  numbers = false;
  symbols = false;
  length = 0;

  onButtonClick() {
    this.clicked++;
    this.password = 'Password' + this.clicked + this.letters + this.numbers + this.symbols + this.length;
  }

  onChangeUseLetters() {
    this.letters = !this.letters;
  }

  onChangeUseNumbers() {
    this.numbers = !this.numbers;
  }

  onChangeUseSymbols() {
    this.symbols = !this.symbols;
  }

  onInputSetLength(length: number) {
    this.length = length;
  }
}
<h1>Password Generator</h1>

<div>
  <label>Enter Length</label>
  <input (input)="onInputSetLength($event.target.value)" />
</div>

<div>
  <label>Use Letters</label>
  <input type="checkbox" (change)="onChangeUseLetters()" />
</div>

<div>
  <label>Use Numbers</label>
  <input type="checkbox" (change)="onChangeUseNumbers()" />
</div>

<div>
  <label>Use Symbols</label>
  <input type="checkbox" (change)="onChangeUseSymbols()" />
</div>

<div>
  <button (click)="onButtonClick()">Generate!</button>
</div>

<div>
  <label>Your Password</label>
  <input [value]="password" />
</div>
